/**
 * @fileoverview Card Texture Generation Utilities for OGL
 *
 * This module provides utilities for generating Canvas-based textures used
 * in the InfiniteGridClass system. Each card requires two textures:
 *
 * 1. Foreground Texture: Contains the main card content (title, image, tags, date)
 * 2. Background Texture: Contains a blurred, darkened version of the card image
 *
 * The textures are generated using HTML5 Canvas 2D API and converted to
 * OGL Textures with proper configuration.
 *
 * Key Features:
 * - Automatic text truncation with ellipsis
 * - Image loading with fallback handling
 * - Styled tag pills with rounded corners
 * - Responsive layout within fixed canvas dimensions
 * - Blur effects for background textures
 * - Proper error handling for failed image loads
 *
 * Usage:
 * ```typescript
 * import { generateForegroundTexture, generateBackgroundTexture } from './createTexture';
 *
 * const cardData = {
 *   title: "Project Title",
 *   image: "/path/to/image.jpg",
 *   tags: ["web", "ogl"],
 *   date: "2024"
 * };
 *
 * const foregroundTexture = await generateForegroundTexture(cardData, gl);
 * const backgroundTexture = await generateBackgroundTexture(cardData, gl);
 * ```
 */

// Card Texture Generation Utilities for OGL

import { Texture, Renderer } from "ogl"; // Required for OGL Texture

/**
 * Represents the data structure for a single card/tile
 * This interface must match the CardData interface in InfiniteGridClass.ts
 */
interface CardData {
  /** The main title text displayed prominently on the card */
  title: string;
  /** Badge text (currently not implemented in the rendering pipeline) */
  badge: string;
  /** Detailed description text for the card content (optional) */
  description?: string;
  /** Array of tag strings that will be displayed as styled pills */
  tags: string[];
  /** Date string displayed in the bottom-right corner */
  date: string;
  /** Optional image URL - falls back to '/photo.png' if not provided */
  image?: string;
}

/**
 * Canvas dimensions for all generated textures
 * These dimensions affect the resolution and memory usage of the textures
 */
const cardWidth = 512;
const cardHeight = 512;
const padding = 30;

/**
 * Creates a canvas element with 2D rendering context
 *
 * This helper function ensures consistent canvas setup across all texture
 * generation functions and provides proper error handling for context creation.
 *
 * @returns Object containing the canvas element and its 2D context
 * @throws {Error} If 2D context creation fails
 */
function createCanvasContext(): { canvas: HTMLCanvasElement; ctx: CanvasRenderingContext2D } {
  const canvas = document.createElement("canvas");
  canvas.width = cardWidth;
  canvas.height = cardHeight;
  const ctx = canvas.getContext("2d");
  if (!ctx) {
    throw new Error("Failed to get 2D context for canvas");
  }
  return { canvas, ctx };
}

// Option 1: Pre-generate textures once, reuse them
const textureCache = new Map<string, Texture>();

/**
 * Generates the foreground texture for a card using Canvas 2D API
 *
 * This function creates the main visible content of each card including:
 * - Title text with automatic truncation and ellipsis
 * - Main image with aspect ratio preservation and centering
 * - Styled tag pills at the bottom
 * - Date text in the bottom-right corner
 * - Border outline around the entire card
 *
 * The generated texture is used for the front-facing mesh that users
 * can interact with (hover and click).
 *
 * @param data - Card data containing title, image, tags, date, etc.
 * @param renderer - OGL Renderer for texture creation
 * @returns Promise resolving to an OGL Texture
 *
 * @example
 * ```typescript
 * const cardData = {
 *   title: "Amazing Project",
 *   image: "/images/project.jpg",
 *   tags: ["web", "ogl"],
 *   date: "2024",
 *   badge: "NEW",
 *   description: "A cool project"
 * };
 * const texture = await generateForegroundTexture(cardData, renderer);
 * ```
 */
export async function generateForegroundTexture(
  data: CardData,
  renderer: Renderer,
): Promise<Texture> {
  const cacheKey = `${data.title}-${data.tags?.join("-")}`;
  if (textureCache.has(cacheKey)) {
    return textureCache.get(cacheKey)!;
  }

  const { canvas, ctx } = createCanvasContext();

  // Set default styles
  ctx.fillStyle = "white";
  ctx.strokeStyle = "rgba(60, 60, 60, 1)";
  ctx.lineWidth = 1;

  // Card background and border (transparent for foreground to show background)
  ctx.beginPath();
  ctx.rect(0, 0, cardWidth, cardHeight);
  ctx.stroke(); // Draw border
  // ctx.fill() is not needed as background is transparent

  let currentY = padding;

  // Title Text
  ctx.font = "24px Arial, sans-serif";
  ctx.fillStyle = "white";
  ctx.textBaseline = "top";

  // Measure text to determine actual width
  const titleText = data.title;
  const titleMaxWidth = cardWidth - padding * 2;

  // For `ellipsis` and `wrap: 'none'`, we need to manually truncate
  let truncatedTitle = titleText;
  let textMetrics = ctx.measureText(truncatedTitle);

  // Simple truncation if text exceeds maxWidth
  while (textMetrics.width > titleMaxWidth && truncatedTitle.length > 3) {
    truncatedTitle = truncatedTitle.substring(0, truncatedTitle.length - 4) + "...";
    textMetrics = ctx.measureText(truncatedTitle);
  }
  ctx.fillText(truncatedTitle, padding, currentY);

  const headerHeight = 24; // Assuming 24px font height is a good approximation for header height

  currentY += headerHeight + 30; // Move Y cursor down

  const topElementsMaxY = currentY;
  const bottomReservedSpace = 100;
  const availableImageHeight = cardHeight - topElementsMaxY - bottomReservedSpace;
  const availableImageWidth = cardWidth - padding * 2;

  // Image Loading and Placement
  const imageObj = new Image();
  imageObj.crossOrigin = "anonymous";
  imageObj.src = data.image || "/photo.png"; // Fallback image

  const loadImagePromise = new Promise<void>((resolve) => {
    imageObj.onload = () => {
      let imgWidth = imageObj.naturalWidth;
      let imgHeight = imageObj.naturalHeight;
      const naturalAspectRatio = imgWidth / imgHeight;

      // Scale image to fit within available space, maintaining aspect ratio
      if (imgWidth > availableImageWidth || imgHeight > availableImageHeight) {
        if (availableImageWidth / naturalAspectRatio <= availableImageHeight) {
          imgWidth = availableImageWidth;
          imgHeight = availableImageWidth / naturalAspectRatio;
        } else {
          imgHeight = availableImageHeight;
          imgWidth = availableImageHeight * naturalAspectRatio;
        }
      }

      const imageX = padding + (availableImageWidth - imgWidth) / 2;
      const imageY = topElementsMaxY + (availableImageHeight - imgHeight) / 2;

      // Draw image (no direct cornerRadius for images in vanilla canvas,
      // you'd need to clip the path if truly desired. For simplicity, we draw directly).
      ctx.drawImage(imageObj, imageX, imageY, imgWidth, imgHeight);
      resolve();
    };

    imageObj.onerror = () => {
      console.error("Failed to load foreground image:", imageObj.src);
      // Placeholder text on image load error
      ctx.fillStyle = "gray";
      ctx.font = "30px Arial";
      ctx.textAlign = "center";
      ctx.textBaseline = "middle";
      ctx.fillText("Image Error", cardWidth / 2, cardHeight / 2 - 50);
      resolve(); // Resolve to allow card generation to continue
    };
  });

  await loadImagePromise; // Wait for the image to load or fail

  // Tags
  let currentXForTags = padding;
  const tagFontSize = 16;
  const tagPaddingX = 15;
  const tagPaddingY = 8;
  const tagGap = 10;

  const tagsY = cardHeight - padding - tagFontSize - tagPaddingY;
  data.tags.forEach((tagText) => {
    ctx.font = `${tagFontSize}px Helvetica, Arial, sans-serif`;
    ctx.textBaseline = "middle"; // Align text vertically in the middle of the shape

    const textToDraw = `#${tagText.toUpperCase()}`;
    const textMetrics = ctx.measureText(textToDraw);
    const tagLabelWidth = textMetrics.width;

    const tagShapeWidth = tagLabelWidth + tagPaddingX;
    const tagShapeHeight = tagFontSize + tagPaddingY;

    // Draw rounded rectangle for tag shape
    ctx.fillStyle = "rgba(248,250, 252, 0.15)";
    drawRoundedRect(ctx, currentXForTags, tagsY, tagShapeWidth, tagShapeHeight, tagShapeHeight / 2); // Use half height for perfect pill shape
    ctx.fill();

    // Draw tag text
    ctx.fillStyle = "white";
    ctx.textAlign = "center";
    ctx.fillText(textToDraw, currentXForTags + tagShapeWidth / 2, tagsY + tagShapeHeight / 2); // Center text in shape

    currentXForTags += tagShapeWidth + tagGap;
  });

  // Date
  ctx.font = "20px Arial, sans-serif";
  ctx.fillStyle = "rgba(255, 255, 255, 1)";
  ctx.textAlign = "right"; // Align text to the right
  ctx.textBaseline = "bottom"; // Align text to the bottom of its bounding box
  ctx.fillText(data.date, cardWidth - padding, cardHeight - padding);

  const texture = new Texture(renderer.gl, {
    image: canvas,
    generateMipmaps: false,
    flipY: false,
  });

  textureCache.set(cacheKey, texture);
  return texture;
}

/**
 * Generates the background texture for a card using Canvas 2D API
 *
 * This function creates a blurred, darkened version of the card's image
 * that serves as the background layer visible during hover effects.
 * The background provides visual depth and context while maintaining
 * readability of the foreground content.
 *
 * Processing steps:
 * 1. Loads the same image used in the foreground
 * 2. Scales it up for better blur coverage
 * 3. Applies canvas blur filter
 * 4. Adds a semi-transparent dark overlay
 * 5. Falls back to solid color if image loading fails
 *
 * @param data - Card data containing the image URL
 * @param renderer - OGL Renderer for texture creation
 * @returns Promise resolving to an OGL Texture for background layer
 *
 * @example
 * ```typescript
 * const backgroundTexture = await generateBackgroundTexture(cardData, renderer);
 * // Use this texture for the background mesh with shader material
 * ```
 */
export async function generateBackgroundTexture(
  data: CardData,
  renderer: Renderer,
): Promise<Texture> {
  const { canvas, ctx } = createCanvasContext();

  // Start with transparent background - image will fill the canvas
  // Alternative: ctx.fillStyle = 'rgba(0,0,0,0.5)' for solid fallback

  const backgroundImageObj = new Image();
  backgroundImageObj.crossOrigin = "Anonymous"; // Enable CORS for external images
  backgroundImageObj.src = data.image || "/photo.png"; // Use same image as foreground

  const loadBackgroundImagePromise = new Promise<void>((resolve) => {
    backgroundImageObj.onload = () => {
      const backgroundScale = 2.0; // Make background image larger for blur effect
      const bgImgWidth = backgroundImageObj.naturalWidth * backgroundScale;
      const bgImgHeight = backgroundImageObj.naturalHeight * backgroundScale;

      // Draw the image first
      ctx.drawImage(
        backgroundImageObj,
        (cardWidth - bgImgWidth) / 2,
        (cardHeight - bgImgHeight) / 2,
        bgImgWidth,
        bgImgHeight,
      );

      // Apply blur directly on the canvas content
      // Note: blur performance and quality can vary between browsers.
      // For more control/consistency, you might apply blur in a shader or pre-process images.
      ctx.filter = "blur(10px)"; // Adjust blur radius as needed
      ctx.drawImage(canvas, 0, 0); // Redraw the canvas content with blur
      ctx.filter = "none"; // Reset filter for subsequent draws

      // Add a semi-transparent overlay to darken/blend the background
      ctx.fillStyle = "rgba(0,0,0,0.4)"; // Dark overlay
      ctx.fillRect(0, 0, cardWidth, cardHeight);

      resolve();
    };

    backgroundImageObj.onerror = () => {
      console.warn("Failed to load background image:", backgroundImageObj.src);
      // Fallback to a solid color background if image fails to load
      // ctx.fillStyle = data.color1 || 'rgba(50,50,50,0.5)'; // Use a default dark gray if data.color1 is not present
      ctx.fillStyle = "rgba(0,0,0,0.5)"; // Fallback to semi-transparent black
      ctx.fillRect(0, 0, cardWidth, cardHeight);
      resolve();
    };
  });

  await loadBackgroundImagePromise; // Wait for background image to load or fail

  const backgroundTexture = new Texture(renderer.gl, {
    image: canvas,
    generateMipmaps: false,
    flipY: false,
  });

  return backgroundTexture;
}

/**
 * Helper function to draw a rounded rectangle using Canvas 2D API
 *
 * Canvas doesn't have a built-in rounded rectangle method, so this
 * function uses quadratic curves to create smooth corners. This is
 * used for drawing the tag pill backgrounds.
 *
 * @param ctx - The 2D rendering context to draw on
 * @param x - X coordinate of the top-left corner
 * @param y - Y coordinate of the top-left corner
 * @param width - Width of the rectangle
 * @param height - Height of the rectangle
 * @param radius - Corner radius in pixels
 */
function drawRoundedRect(
  ctx: CanvasRenderingContext2D,
  x: number,
  y: number,
  width: number,
  height: number,
  radius: number,
): void {
  ctx.beginPath();
  ctx.moveTo(x + radius, y);
  ctx.lineTo(x + width - radius, y);
  ctx.quadraticCurveTo(x + width, y, x + width, y + radius);
  ctx.lineTo(x + width, y + height - radius);
  ctx.quadraticCurveTo(x + width, y + height, x + width - radius, y + height);
  ctx.lineTo(x + radius, y + height);
  ctx.quadraticCurveTo(x, y + height, x, y + height - radius);
  ctx.lineTo(x, y + radius);
  ctx.quadraticCurveTo(x, y, x + radius, y);
  ctx.closePath();
}

/**
 * Convenience function to generate both foreground and background textures
 *
 * This function generates both texture types in parallel for efficiency.
 * While the individual generation functions are typically used separately
 * in the main grid system, this function can be useful for testing or
 * simpler use cases.
 *
 * @param data - Card data for texture generation
 * @param renderer - OGL Renderer for texture creation
 * @returns Promise resolving to an object with both texture types
 *
 * @example
 * ```typescript
 * const { foreground, background } = await generateCardTextures(cardData, renderer);
 * // Use foreground for main mesh, background for hover effect
 * ```
 */
export async function generateCardTextures(
  data: CardData,
  renderer: Renderer,
): Promise<{
  foreground: Texture;
  background: Texture;
}> {
  const [foreground, background] = await Promise.all([
    generateForegroundTexture(data, renderer),
    generateBackgroundTexture(data, renderer),
  ]);
  return { foreground, background };
}

/* eslint-disable @typescript-eslint/no-explicit-any */
/**
 * @fileoverview DisposalManager - Resource Cleanup System for InfiniteGrid
 *
 * This module handles the complete disposal and cleanup of all resources
 * used by the infinite grid system. It ensures proper memory management
 * by disposing of WebGL resources, clearing references, and removing
 * event listeners to prevent memory leaks.
 *
 * Key Responsibilities:
 * - Animation frame cleanup
 * - Event listener removal
 * - OGL resource disposal (meshes, programs, textures)
 * - Post-processing resource cleanup
 * - Renderer and canvas cleanup
 * - Data structure clearing
 * - GSAP animation cleanup
 */

import { Mesh, Transform, Renderer, Camera, RenderTarget } from "ogl";
import type { CardTexturePair, TileGroupData } from "./types.ts";
import { CustomPostProcessShader } from "./PostProcessShader";
import { EventHandler } from "./EventHandler";
import { GridManager } from "./GridManager";

/**
 * Interface defining the required properties that the DisposalManager
 * needs to access from the main InfiniteGrid class for cleanup
 */
export interface DisposableHost {
  // Animation and timing
  animationFrameId: number | null;

  // Event handling
  eventHandler?: EventHandler;

  // Grid management
  gridManager: GridManager;

  // OGL core objects
  scene: Transform;
  camera: Camera | null;
  renderer: Renderer | null;

  // Post-processing
  postProcessShader: CustomPostProcessShader | null;
  sceneRenderTarget: RenderTarget | null;

  // Scene objects and data
  groupObjects: Transform[];
  foregroundMeshMap: Map<string, Mesh>;
  backgroundMeshMap: Map<string, Mesh>;
  cardTextures: CardTexturePair[];
  staticUniforms: Map<string, any>;
  tileGroupsData: TileGroupData[];

  // Interaction objects
  raycast: any;
  pointer: any;
  scrollTracker: any;

  // Container reference
  container: HTMLElement;
} /**
 * DisposalManager handles the complete cleanup of InfiniteGrid resources
 *
 * This class provides a systematic approach to disposing of all resources
 * used by the infinite grid system, ensuring no memory leaks occur when
 * the grid is no longer needed.
 *
 * The disposal process follows this order:
 * 1. Stop animation loops
 * 2. Remove event listeners
 * 3. Dispose 3D scene objects and materials
 * 4. Clean up post-processing resources
 * 5. Remove renderer and canvas
 * 6. Clear data structures and references
 * 7. Clean up animation systems
 */
export class DisposalManager {
  private host: DisposableHost;

  /**
   * Creates a new DisposalManager instance
   * @param host - The main grid class that provides resources to dispose
   */
  constructor(host: DisposableHost) {
    this.host = host;
  }

  /**
   * Performs complete disposal of all grid resources
   *
   * This method should be called when the grid is no longer needed,
   * such as when navigating away from the page or unmounting a component.
   *
   * @example
   * ```typescript
   * // In a Vue component's onBeforeUnmount
   * onBeforeUnmount(() => {
   *   if (gridInstance) {
   *     disposalManager.dispose();
   *   }
   * });
   * ```
   */
  public dispose(): void {
    // Step 1: Cancel animation loops
    this.stopAnimationLoop();

    // Step 2: Remove event listeners
    this.cleanupEventListeners();

    // Step 3: Clear grid manager
    this.cleanupGridManager();

    // Step 4: Dispose 3D scene objects
    this.disposeSceneObjects();

    // Step 5: Clean up post-processing
    this.disposePostProcessing();

    // Step 6: Dispose renderer and canvas
    this.disposeRenderer();

    // Step 7: Clear data structures
    this.clearDataStructures();

    // Step 8: Clean up animation systems
    this.cleanupAnimationSystems();
  }

  /**
   * Cancels the main animation frame loop
   */
  private stopAnimationLoop(): void {
    if (this.host.animationFrameId) {
      cancelAnimationFrame(this.host.animationFrameId);
      this.host.animationFrameId = null;
    }
  }

  /**
   * Removes all event listeners through the EventHandler
   */
  private cleanupEventListeners(): void {
    if (this.host.eventHandler) {
      this.host.eventHandler.removeEventListeners();
    }
  }

  /**
   * Clears the grid manager and all its managed resources
   */
  private cleanupGridManager(): void {
    if (this.host.gridManager) {
      this.host.gridManager.clear();
    }
  }

  /**
   * Disposes all 3D scene objects including meshes, geometries, and programs
   */
  private disposeSceneObjects(): void {
    // Clean up group objects and their children
    this.host.groupObjects.forEach((group) => {
      this.disposeTransformAndChildren(group);
    });

    // Clear the main scene
    this.disposeTransformAndChildren(this.host.scene);

    // Clear mesh maps
    this.host.foregroundMeshMap.forEach((mesh) => {
      this.disposeMesh(mesh);
    });
    this.host.backgroundMeshMap.forEach((mesh) => {
      this.disposeMesh(mesh);
    });
  }

  /**
   * Recursively disposes a Transform and all its children
   * @param transform - The transform to dispose
   */
  private disposeTransformAndChildren(transform: Transform): void {
    if (!transform) return;

    // Dispose all children first
    transform.traverse((child) => {
      if (child instanceof Mesh) {
        this.disposeMesh(child);
      }
    });

    // Clear parent-child relationships
    (transform as any).parent = null;
    (transform as any).children = [];
  }

  /**
   * Disposes a single mesh and its resources
   * @param mesh - The mesh to dispose
   */
  private disposeMesh(mesh: Mesh): void {
    if (!mesh) return;

    // Clear geometry reference (OGL manages WebGL resources automatically)
    (mesh as any).geometry = null;

    // Clear program reference
    if ((mesh as any).program) {
      (mesh as any).program = null;
    }

    // Clear user data
    (mesh as any).userData = null;

    // Clear parent reference
    (mesh as any).parent = null;
  }

  /**
   * Disposes post-processing resources
   */
  private disposePostProcessing(): void {
    if (this.host.postProcessShader) {
      this.host.postProcessShader.dispose();
      this.host.postProcessShader = null;
    }

    if (this.host.sceneRenderTarget) {
      // OGL RenderTarget disposal is handled automatically
      this.host.sceneRenderTarget = null;
    }
  }

  /**
   * Disposes the renderer and removes canvas from DOM
   */
  private disposeRenderer(): void {
    if (this.host.renderer) {
      // Remove canvas from DOM if it's still attached
      const canvas = this.host.renderer.gl.canvas;
      if (canvas.parentNode === this.host.container) {
        this.host.container.removeChild(canvas);
      }

      // Clear renderer reference (WebGL context cleanup is automatic)
      this.host.renderer = null;
    }

    // Clear camera reference
    this.host.camera = null;
  }

  /**
   * Clears all data structures and maps
   */
  private clearDataStructures(): void {
    // Clear texture references (OGL handles WebGL texture cleanup)
    this.host.cardTextures.forEach((texturePair) => {
      texturePair.foreground = null;
      texturePair.background = null;
    });

    // Clear all arrays and maps
    this.host.groupObjects = [];
    this.host.foregroundMeshMap.clear();
    this.host.backgroundMeshMap.clear();
    this.host.staticUniforms.clear();
    this.host.cardTextures = [];
    this.host.tileGroupsData = [];

    // Clear interaction objects
    this.host.raycast = null;
    this.host.pointer = null;
  }

  /**
   * Cleans up GSAP animations and trackers
   */
  private cleanupAnimationSystems(): void {
    if (this.host.scrollTracker) {
      this.host.scrollTracker.kill();
      this.host.scrollTracker = null;
    }

    // Kill any remaining GSAP animations to prevent callbacks
    // This is a safety measure in case any animations are still running
    try {
      // Note: This would require importing gsap, but we'll let the main class handle it
      // gsap.killTweensOf(this.host);
    } catch (error) {
      console.warn("Error killing GSAP tweens during disposal:", error);
    }
  }

  /**
   * Performs a partial cleanup that preserves the core structure
   * but clears dynamic content. Useful for reinitialization scenarios.
   */
  public partialCleanup(): void {
    // Stop animations but don't destroy everything
    this.stopAnimationLoop();

    // Clear dynamic content
    this.host.cardTextures.forEach((texturePair) => {
      texturePair.foreground = null;
      texturePair.background = null;
    });
    this.host.cardTextures = [];

    // Clear mesh content but keep structure
    this.host.foregroundMeshMap.clear();
    this.host.backgroundMeshMap.clear();
    this.host.staticUniforms.clear();
  }

  /**
   * Validates that all resources have been properly disposed
   * Useful for debugging memory leaks
   */
  public validateDisposal(): boolean {
    const issues: string[] = [];

    if (this.host.animationFrameId !== null) {
      issues.push("Animation frame still active");
    }

    if (this.host.renderer !== null) {
      issues.push("Renderer not disposed");
    }

    if (this.host.camera !== null) {
      issues.push("Camera not disposed");
    }

    if (this.host.postProcessShader !== null) {
      issues.push("Post-process shader not disposed");
    }

    if (this.host.groupObjects.length > 0) {
      issues.push("Group objects not cleared");
    }

    if (this.host.foregroundMeshMap.size > 0) {
      issues.push("Foreground mesh map not cleared");
    }

    if (this.host.backgroundMeshMap.size > 0) {
      issues.push("Background mesh map not cleared");
    }

    if (this.host.cardTextures.length > 0) {
      issues.push("Card textures not cleared");
    }

    if (this.host.scrollTracker !== null) {
      issues.push("Scroll tracker not disposed");
    }

    if (issues.length > 0) {
      console.warn("Disposal validation failed:", issues);
      return false;
    }
    return true;
  }
}

/* eslint-disable @typescript-eslint/no-explicit-any */
/**
 * @fileoverview EventHandler - Event Management System for InfiniteGrid
 *
 * This module handles all user interactions for the infinite grid system,
 * including pointer events (mouse/touch), hover effects, click detection,
 * and window resize handling.
 *
 * Key Responsibilities:
 * - Pointer down/move/up event handling for scrolling
 * - Touch event support with proper coordinate handling
 * - Hover state management with background blur effects
 * - Click detection with movement threshold
 * - Window resize handling for responsive behavior
 * - Event listener lifecycle management
 */

import { gsap } from "gsap";
import { Mesh, Vec2, Raycast } from "ogl";
import type {
  Position2D,
  ScrollState,
  TileUserData,
  TileClickEventDetail,
  CardData,
} from "./types.ts";

/**
 * Interface defining the required properties and methods that the EventHandler
 * needs to access from the main InfiniteGrid class
 */
export interface EventHandlerHost {
  // Container and interaction state
  container: HTMLElement;
  pointer: Vec2;
  raycast: Raycast;
  camera: any;
  renderer: any;

  // Post processing
  sceneRenderTarget: any;
  postProcessShader: any;

  // Scroll and movement state
  scroll: ScrollState;
  isDown: boolean;
  isHoveringCanvas: boolean;
  hasMovedSignificantly: boolean;
  startPosition: Position2D;
  scrollPosition: Position2D;
  scrollTracker: any;

  // Hover state
  currentHoveredTileKey: string;
  backgroundMeshMap: Map<string, Mesh>;
  foregroundMeshMap: Map<string, Mesh>;

  // Configuration
  options: {
    baseCameraZ: number;
  };

  // Animation constants
  maxClickMovement: number;
  hoverTransitionDuration: number;
  hoverEase: string;
  initialBackgroundOpacity: number;
  hoveredBackgroundOpacity: number;

  // Card data
  cardData: CardData[];

  // Methods that need to be called
  updatePositions(): void;
  animateInertiaScroll(vx?: number | string, vy?: number | string): void;
  getTileKeyFromMesh(mesh: Mesh): string;
  getCardDataForTile(groupIndex: number, tileIndex: number): CardData;
  getInteractiveMeshes(): Mesh[];
  updatePointerCoordinates(clientX: number, clientY: number): void;
  performRaycast(): Mesh[];
  fadeInBackground(mesh: Mesh): void;
  fadeOutBackground(mesh: Mesh): void;
}

/**
 * EventHandler class manages all user interactions for the infinite grid
 *
 * This class encapsulates event handling logic including:
 * - Mouse and touch input processing
 * - Hover state management with visual feedback
 * - Click detection with drag threshold
 * - Responsive window resize handling
 * - Event listener lifecycle management
 */
export class EventHandler {
  private host: EventHandlerHost;
  private isInitialized: boolean = false;

  /**
   * Creates a new EventHandler instance
   * @param host - The main grid class that provides required properties and methods
   */
  constructor(host: EventHandlerHost) {
    this.host = host;

    // Bind all event handler methods to maintain proper 'this' context
    this.onPointerDown = this.onPointerDown.bind(this);
    this.onPointerMove = this.onPointerMove.bind(this);
    this.onPointerUp = this.onPointerUp.bind(this);
    this.onPointerOut = this.onPointerOut.bind(this);
    this.onWindowResize = this.onWindowResize.bind(this);
    this.handleMouseClick = this.handleMouseClick.bind(this);
    this.handleTouchEnd = this.handleTouchEnd.bind(this);
  }

  /**
   * Initializes event listeners
   * Should be called once after the grid is set up
   */
  public initialize(): void {
    if (this.isInitialized) {
      console.warn("EventHandler already initialized");
      return;
    }

    this.addEventListeners();
    this.isInitialized = true;
  }

  /**
   * Handles pointer down events (mouse button press or touch start)
   * Initiates drag interaction and camera zoom
   */
  private onPointerDown(e: MouseEvent | TouchEvent): void {
    e.preventDefault();

    this.host.currentHoveredTileKey = "";
    this.host.isDown = true;
    this.host.hasMovedSignificantly = false;
    this.host.scrollPosition.x = this.host.scroll.current.x;
    this.host.scrollPosition.y = this.host.scroll.current.y;

    const clientX = "touches" in e ? e.touches[0].clientX : e.clientX;
    const clientY = "touches" in e ? e.touches[0].clientY : e.clientY;

    this.host.startPosition.x = clientX;
    this.host.startPosition.y = clientY;

    // Update pointer coordinates for raycasting
    this.host.updatePointerCoordinates(clientX, clientY);

    if (this.host.camera) {
      gsap.to(this.host.camera.position, {
        z: this.host.options.baseCameraZ * 1.3,
        duration: 0.3,
        ease: "power2.out",
        overwrite: true,
      });
    }
  }

  /**
   * Handles pointer move events (mouse move or touch move)
   * Processes scrolling or hover states depending on interaction state
   */
  private onPointerMove(e: MouseEvent | TouchEvent): void {
    if (!this.host.isDown) {
      this.handleHover(e);
      return;
    }

    const clientX = "touches" in e ? e.touches[0].clientX : e.clientX;
    const clientY = "touches" in e ? e.touches[0].clientY : e.clientY;

    // Check if movement is significant enough to disable click
    const movementDistance = Math.sqrt(
      Math.pow(clientX - this.host.startPosition.x, 2) +
        Math.pow(clientY - this.host.startPosition.y, 2),
    );

    if (movementDistance > this.host.maxClickMovement) {
      this.host.hasMovedSignificantly = true;
    }

    const distanceX = (this.host.startPosition.x - clientX) * this.host.scroll.scale;
    const distanceY = (this.host.startPosition.y - clientY) * this.host.scroll.scale;

    gsap.to(this.host.scroll.current, {
      x: this.host.scrollPosition.x - distanceX,
      y: this.host.scrollPosition.y + distanceY,
      duration: 0.1,
      ease: "power1.out",
      overwrite: true,
      onUpdate: () => this.host.updatePositions(),
    });

    this.host.scroll.last.x = this.host.scroll.current.x;
    this.host.scroll.last.y = this.host.scroll.current.y;
  }

  /**
   * Handles pointer up events (mouse button release or touch end)
   * Ends drag interaction and applies inertia scrolling
   */
  private onPointerUp(e?: MouseEvent | TouchEvent): void {
    this.host.isDown = false;

    // Clear hover state when pointer is lifted
    if (this.host.currentHoveredTileKey) {
      const mesh = this.host.backgroundMeshMap.get(this.host.currentHoveredTileKey);
      if (mesh) {
        this.host.fadeOutBackground(mesh);
      }
      this.host.currentHoveredTileKey = "";
    }

    if (this.host.camera) {
      gsap.to(this.host.camera.position, {
        z: this.host.options.baseCameraZ,
        duration: 0.3,
        ease: "power2.out",
        overwrite: true,
      });
    }

    const vx = this.host.scrollTracker.get("x");
    const vy = this.host.scrollTracker.get("y");

    this.host.animateInertiaScroll(vx, vy);
  }

  /**
   * Handles pointer leaving the canvas area
   * Clears hover states when mouse exits
   */
  private onPointerOut(e: MouseEvent): void {
    this.host.isHoveringCanvas = false;

    // Clear hover state when pointer leaves canvas
    if (this.host.currentHoveredTileKey) {
      const mesh = this.host.backgroundMeshMap.get(this.host.currentHoveredTileKey);
      if (mesh) {
        this.host.fadeOutBackground(mesh);
      }
      this.host.currentHoveredTileKey = "";
    }
  }

  /**
   * Handles window resize events
   * Updates camera aspect ratio and renderer size
   */
  private onWindowResize(): void {
    const newWidth = this.host.container.clientWidth;
    const newHeight = this.host.container.clientHeight;

    if (this.host.camera) {
      this.host.camera.aspect = newWidth / newHeight;
      this.host.camera.perspective({ aspect: newWidth / newHeight });
    }

    if (this.host.renderer) {
      this.host.renderer.setSize(newWidth, newHeight);
    }

    // Update post-processing render targets
    if (this.host.sceneRenderTarget) {
      this.host.sceneRenderTarget.setSize(newWidth, newHeight);
    }
    if (this.host.postProcessShader) {
      this.host.postProcessShader.resize(newWidth, newHeight);
    }
  }

  /**
   * Handles hover effects when not dragging
   * Manages background blur fade in/out based on tile intersection
   */
  private handleHover(e: MouseEvent | TouchEvent): void {
    const clientX = "touches" in e ? e.touches[0].clientX : e.clientX;
    const clientY = "touches" in e ? e.touches[0].clientY : e.clientY;

    // Update pointer coordinates for raycasting
    this.host.updatePointerCoordinates(clientX, clientY);

    // Perform raycasting to find hovered tiles
    const hits = this.host.performRaycast();

    // Handle hover state changes
    const newHoveredTileKey = hits.length > 0 ? this.host.getTileKeyFromMesh(hits[0]) : "";

    // If hovering over a different tile
    if (newHoveredTileKey !== this.host.currentHoveredTileKey) {
      // Fade out previous hovered tile
      if (this.host.currentHoveredTileKey) {
        const prevMesh = this.host.backgroundMeshMap.get(this.host.currentHoveredTileKey);
        if (prevMesh) {
          this.host.fadeOutBackground(prevMesh);
        }
      }

      // Fade in new hovered tile
      if (newHoveredTileKey) {
        const newMesh = this.host.backgroundMeshMap.get(newHoveredTileKey);
        if (newMesh) {
          this.host.fadeInBackground(newMesh);
        }
      }

      this.host.currentHoveredTileKey = newHoveredTileKey;
    }
  }

  /**
   * Handles mouse click events
   * Processes clicks only if no significant movement occurred
   */
  private handleMouseClick(e: MouseEvent): void {
    // Don't handle clicks if the user has moved significantly (drag vs click)
    if (this.host.hasMovedSignificantly) {
      return;
    }

    // Update pointer coordinates for raycasting
    this.host.updatePointerCoordinates(e.clientX, e.clientY);

    // Perform click logic
    this.performTileClick();
  }

  /**
   * Handles touch end events
   * Combines pointer up logic with click detection for touch devices
   */
  private handleTouchEnd(e: TouchEvent): void {
    // Call the original onPointerUp logic first
    this.onPointerUp(e);

    // Don't handle clicks if the user has moved significantly (drag vs click)
    if (this.host.hasMovedSignificantly) {
      return;
    }

    // For touch end, use changedTouches to get the final touch position
    if (e.changedTouches && e.changedTouches.length > 0) {
      const touch = e.changedTouches[0];
      this.host.updatePointerCoordinates(touch.clientX, touch.clientY);

      // Perform click logic
      this.performTileClick();
    }
  }

  /**
   * Performs tile click detection and event dispatching
   * Uses raycasting to determine which tile was clicked
   */
  private performTileClick(): void {
    // Perform raycasting to find clicked tiles
    const hits = this.host.performRaycast();

    if (hits.length > 0) {
      const clickedMesh = hits[0]; // Get the closest hit
      const userData = (clickedMesh as any).userData as TileUserData;

      if (userData) {
        // Get the card data for the clicked tile
        const cardData = this.host.getCardDataForTile(userData.groupIndex, userData.tileIndex);

        // Create and dispatch custom event
        const eventDetail: TileClickEventDetail = {
          groupIndex: userData.groupIndex,
          tileIndex: userData.tileIndex,
          cardData: cardData,
        };

        const customEvent = new CustomEvent<TileClickEventDetail>("tileClicked", {
          detail: eventDetail,
          bubbles: true,
          cancelable: true,
        });

        this.host.container.dispatchEvent(customEvent);

        // Log for debugging
        window.alert(
          "Tile clicked: " +
            JSON.stringify({
              title: cardData.title,
              groupIndex: userData.groupIndex,
              tileIndex: userData.tileIndex,
            }),
        );
      }
    }
  }

  /**
   * Adds all event listeners to the container and window
   * Should be called once during initialization
   */
  private addEventListeners(): void {
    this.host.container.addEventListener("mousedown", this.onPointerDown);
    this.host.container.addEventListener("mousemove", this.onPointerMove);
    this.host.container.addEventListener("mouseup", this.onPointerUp);
    this.host.container.addEventListener("mouseleave", this.onPointerOut);
    this.host.container.addEventListener("touchstart", this.onPointerDown, { passive: false });
    this.host.container.addEventListener("touchmove", this.onPointerMove, { passive: false });
    this.host.container.addEventListener("touchend", this.handleTouchEnd, { passive: true });

    // Only add click event for mouse interactions
    this.host.container.addEventListener("click", this.handleMouseClick);

    window.addEventListener("resize", this.onWindowResize);
  }

  /**
   * Removes all event listeners
   * Should be called during cleanup/disposal
   */
  public removeEventListeners(): void {
    if (!this.isInitialized) {
      return;
    }

    this.host.container.removeEventListener("mousedown", this.onPointerDown);
    this.host.container.removeEventListener("mousemove", this.onPointerMove);
    this.host.container.removeEventListener("mouseup", this.onPointerUp);
    this.host.container.removeEventListener("mouseleave", this.onPointerOut);
    this.host.container.removeEventListener("touchstart", this.onPointerDown);
    this.host.container.removeEventListener("touchmove", this.onPointerMove);
    this.host.container.removeEventListener("touchend", this.handleTouchEnd);
    this.host.container.removeEventListener("click", this.handleMouseClick);

    window.removeEventListener("resize", this.onWindowResize);

    this.isInitialized = false;
  }

  /**
   * Gets the initialization status
   */
  public get initialized(): boolean {
    return this.isInitialized;
  }
}

/* eslint-disable @typescript-eslint/no-explicit-any */
/**
 * @fileoverview GridManager - Grid Creation and Management System for InfiniteGrid
 *
 * This module handles all aspects of grid creation, tile management, and texture
 * generation for the infinite grid system. It's responsible for:
 *
 * Key Features:
 * - 3x3 tile group initialization for infinite scrolling
 * - Dynamic tile creation with foreground and background meshes
 * - Texture generation and management for card data
 * - Shader program creation for tile rendering
 * - Tile positioning and indexing systems
 * - Card data to texture mapping
 *
 * Architecture:
 * - Creates 9 tile groups arranged in a 3x3 pattern
 * - Each group contains a configurable grid of individual tiles
 * - Each tile has both foreground (content) and background (blur) layers
 * - Textures are generated dynamically from card data using Canvas 2D API
 * - Programs are created with appropriate shaders for each tile type
 */

import { Renderer, Transform, Texture, Program, Mesh, Vec3, Plane } from "ogl";
import { generateForegroundTexture, generateBackgroundTexture } from "./createTexture";
import { gaussianBlurVertexShader, gaussianBlurFragmentShader } from "./shaders";

import type { CardData, TileGroupData, TileUserData, CardTexturePair } from "./types.ts";

/**
 * Interface defining the required properties and methods that the GridManager
 * needs to access from the main InfiniteGrid class
 */
export interface GridManagerHost {
  // Core properties
  renderer: Renderer | null;
  scene: Transform;
  cardData: CardData[];

  // Grid configuration
  GRID_COLS: number;
  GRID_ROWS: number;
  GRID_WIDTH: number;
  GRID_HEIGHT: number;
  TILE_SIZE: number;
  TILE_SPACE: number;

  // Animation constants
  initialBackgroundOpacity: number;

  // Data structures that will be populated
  tileGroupsData: TileGroupData[];
  groupObjects: Transform[];
  foregroundMeshMap: Map<string, Mesh>;
  backgroundMeshMap: Map<string, Mesh>;
  cardTextures: CardTexturePair[];
  staticUniforms: Map<string, any>;
}

/**
 * GridManager handles all grid creation and management functionality
 *
 * This class encapsulates the complex logic of creating and managing
 * the 3x3 infinite grid system including:
 * - Tile group initialization and positioning
 * - Dynamic tile creation with proper materials
 * - Texture generation from card data
 * - Shader program creation and management
 * - Tile indexing and key management
 *
 * The grid system uses a 3x3 pattern of tile groups where each group
 * contains a configurable number of individual tiles. This creates
 * the illusion of infinite content while maintaining performance.
 */
export class GridManager {
  private host: GridManagerHost;
  private isInitialized: boolean = false;

  /**
   * Creates a new GridManager instance
   * @param host - The main grid class that provides required properties and methods
   */
  constructor(host: GridManagerHost) {
    this.host = host;
  }

  /**
   * Initializes the complete grid system
   *
   * This method sets up the entire grid in the correct order:
   * 1. Initialize tile group positions
   * 2. Generate textures for all card data
   * 3. Create all tile meshes with proper materials
   *
   * @returns Promise that resolves when all grid setup is complete
   */
  public async initialize(): Promise<void> {
    if (this.isInitialized) {
      console.warn("GridManager already initialized");
      return;
    }

    this.initializeTileGroups();
    await this.generateTexturesForCardData();
    this.createTiles();

    this.isInitialized = true;
  }

  /**
   * Initializes the 3x3 grid of tile groups for infinite scrolling
   *
   * Creates 9 tile groups arranged in a 3x3 pattern. Each group contains
   * a grid of tiles. As the user scrolls, groups are repositioned to
   * create the illusion of infinite content.
   */
  private initializeTileGroups(): void {
    this.host.tileGroupsData = [];
    for (let r = -1; r <= 1; r++) {
      for (let c = -1; c <= 1; c++) {
        this.host.tileGroupsData.push({
          basePos: new Vec3(this.host.GRID_WIDTH * c, this.host.GRID_HEIGHT * r, 0),
          offset: { x: 0, y: 0 },
        });
      }
    }
  }

  /**
   * Creates all tiles for the grid system
   *
   * For each tile group, creates individual tiles with:
   * - Background mesh (for blur effects)
   * - Foreground mesh (for content display)
   * - Proper positioning and parenting
   * - User data for interaction
   */
  private createTiles(): void {
    if (!this.host.renderer) {
      throw new Error("Renderer not available for tile creation");
    }

    const gl = this.host.renderer.gl;

    this.host.tileGroupsData.forEach((groupData, groupIndex) => {
      const groupObject = new Transform();
      groupObject.position.set(groupData.basePos.x, groupData.basePos.y, groupData.basePos.z);
      groupObject.setParent(this.host.scene);
      this.host.groupObjects[groupIndex] = groupObject;

      const startX = -((this.host.GRID_COLS - 1) / 2) * this.host.TILE_SPACE;
      const startY = ((this.host.GRID_ROWS - 1) / 2) * this.host.TILE_SPACE;

      for (let row = 0; row < this.host.GRID_ROWS; row++) {
        for (let col = 0; col < this.host.GRID_COLS; col++) {
          const x = startX + col * this.host.TILE_SPACE;
          const y = startY - row * this.host.TILE_SPACE;

          const tileIndex = row * this.host.GRID_COLS + col;
          const tileKey = this.getTileKey(groupIndex, tileIndex);

          // Create background mesh (for blur effects)
          this.createBackgroundMesh(gl, groupObject, groupIndex, tileIndex, tileKey, x, y);

          // Create foreground mesh (for content display)
          this.createForegroundMesh(gl, groupObject, groupIndex, tileIndex, tileKey, x, y);
        }
      }
    });
  }

  /**
   * Creates a background mesh for blur effects
   */
  private createBackgroundMesh(
    gl: any, // OGL context
    groupObject: Transform,
    groupIndex: number,
    tileIndex: number,
    tileKey: string,
    x: number,
    y: number,
  ): void {
    const backgroundProgram = this.createBackgroundProgram(groupIndex, tileIndex);
    const backgroundGeometry = new Plane(gl, {
      width: this.host.TILE_SIZE,
      height: this.host.TILE_SIZE,
    });
    const backgroundMesh = new Mesh(gl, {
      geometry: backgroundGeometry,
      program: backgroundProgram,
    });
    backgroundMesh.position.set(x, y, -0.01);
    backgroundMesh.setParent(groupObject);
    this.host.backgroundMeshMap.set(tileKey, backgroundMesh);
  }

  /**
   * Creates a foreground mesh for content display
   */
  private createForegroundMesh(
    gl: any, // OGL context
    groupObject: Transform,
    groupIndex: number,
    tileIndex: number,
    tileKey: string,
    x: number,
    y: number,
  ): void {
    const foregroundProgram = this.createForegroundProgram(groupIndex, tileIndex);
    const foregroundGeometry = new Plane(gl, {
      width: this.host.TILE_SIZE,
      height: this.host.TILE_SIZE,
    });
    const foregroundMesh = new Mesh(gl, {
      geometry: foregroundGeometry,
      program: foregroundProgram,
    });
    foregroundMesh.position.set(x, y, 0);
    foregroundMesh.setParent(groupObject);

    // Store user data for interaction
    (foregroundMesh as any).userData = {
      groupIndex,
      tileIndex,
      tileKey,
    } as TileUserData;

    this.host.foregroundMeshMap.set(tileKey, foregroundMesh);
  }

  /**
   * Generates a unique tile key for indexing
   * @param groupIndex - The index of the tile group (0-8)
   * @param tileIndex - The index of the tile within the group
   * @returns A unique string key for the tile
   */
  public getTileKey(groupIndex: number, tileIndex: number): string {
    return `${groupIndex}-${tileIndex}`;
  }

  /**
   * Calculates the card texture index for a given tile
   * @param groupIndex - The index of the tile group
   * @param tileIndex - The index of the tile within the group
   * @returns The index of the card data to use for this tile
   */
  public getCardTextureIndex(groupIndex: number, tileIndex: number): number {
    const tilesPerGroup = this.host.GRID_COLS * this.host.GRID_ROWS;
    return (groupIndex * tilesPerGroup + tileIndex) % this.host.cardData.length;
  }

  /**
   * Gets the foreground texture for a specific tile
   * @param groupIndex - The index of the tile group
   * @param tileIndex - The index of the tile within the group
   * @returns The foreground texture or null if not available
   */
  public getCardForegroundTexture(groupIndex: number, tileIndex: number): Texture | null {
    if (this.host.cardTextures.length === 0) return null;
    const textureIndex = this.getCardTextureIndex(groupIndex, tileIndex);
    return this.host.cardTextures[textureIndex]?.foreground || null;
  }

  /**
   * Gets the background texture for a specific tile
   * @param groupIndex - The index of the tile group
   * @param tileIndex - The index of the tile within the group
   * @returns The background texture or null if not available
   */
  public getCardBackgroundTexture(groupIndex: number, tileIndex: number): Texture | null {
    if (this.host.cardTextures.length === 0) return null;
    const textureIndex = this.getCardTextureIndex(groupIndex, tileIndex);
    return this.host.cardTextures[textureIndex]?.background || null;
  }

  /**
   * Creates a shader program for background tiles (with blur effects)
   * @param groupIndex - The index of the tile group
   * @param tileIndex - The index of the tile within the group
   * @returns A configured Program for background rendering
   */
  private createBackgroundProgram(groupIndex: number, tileIndex: number): Program {
    if (!this.host.renderer) throw new Error("Renderer not initialized");

    const gl = this.host.renderer.gl;
    const texture = this.getCardBackgroundTexture(groupIndex, tileIndex);
    const texWidth = 512;
    const texHeight = 512;

    const uniforms = {
      map: { value: texture },
      resolution: { value: [texWidth, texHeight] },
      uOpacity: { value: this.host.initialBackgroundOpacity },
    };

    this.host.staticUniforms.set(this.getTileKey(groupIndex, tileIndex), uniforms);

    return new Program(gl, {
      vertex: gaussianBlurVertexShader,
      fragment: gaussianBlurFragmentShader,
      uniforms: uniforms,
      transparent: true,
      cullFace: false,
    });
  }

  /**
   * Creates a shader program for foreground tiles (content display)
   * @param groupIndex - The index of the tile group
   * @param tileIndex - The index of the tile within the group
   * @returns A configured Program for foreground rendering
   */
  private createForegroundProgram(groupIndex: number, tileIndex: number): Program {
    if (!this.host.renderer) throw new Error("Renderer not initialized");

    const gl = this.host.renderer.gl;
    const texture = this.getCardForegroundTexture(groupIndex, tileIndex);

    return new Program(gl, {
      vertex: `
        attribute vec2 uv;
        attribute vec3 position;
        
        uniform mat4 modelViewMatrix;
        uniform mat4 projectionMatrix;
        
        varying vec2 vUv;
        
        void main() {
          // Flip UV coordinates 180 degrees (both X and Y)
          vUv = vec2(uv.x, 1.0 - uv.y);
          gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
        }
      `,
      fragment: `
        precision highp float;
        
        uniform sampler2D map;
        
        varying vec2 vUv;
        
        void main() {
          gl_FragColor = texture2D(map, vUv);
        }
      `,
      uniforms: {
        map: { value: texture },
      },
      transparent: true,
      cullFace: false,
    });
  }

  /**
   * Generates textures for all card data
   *
   * Creates both foreground and background textures for each card
   * using the Canvas 2D API. Textures are cached for efficient reuse.
   *
   * @returns Promise that resolves when all textures are generated
   */
  private async generateTexturesForCardData(): Promise<void> {
    if (!this.host.renderer) throw new Error("Renderer not initialized");

    if (this.host.cardData.length === 0) {
      this.host.cardTextures = [];
      return;
    }

    const texturePromises = this.host.cardData.map(async (card): Promise<CardTexturePair> => {
      const foreground = await generateForegroundTexture(card, this.host.renderer!);
      const background = await generateBackgroundTexture(card, this.host.renderer!);
      return { foreground, background };
    });

    this.host.cardTextures = await Promise.all(texturePromises);
  }

  /**
   * Extracts tile key from a mesh using its userData
   * @param mesh - The mesh to get the tile key from
   * @returns The tile key or empty string if not found
   */
  public getTileKeyFromMesh(mesh: Mesh): string {
    const userData = (mesh as any).userData as TileUserData;
    return userData?.tileKey || "";
  }

  /**
   * Gets the card data for a specific tile
   * @param groupIndex - The group index of the tile
   * @param tileIndex - The tile index within the group
   * @returns The card data for the tile
   */
  public getCardDataForTile(groupIndex: number, tileIndex: number): CardData {
    const cardIndex = this.getCardTextureIndex(groupIndex, tileIndex);
    return (
      this.host.cardData[cardIndex] || {
        title: "Default Card",
        badge: "",
        description: "No data available",
        tags: [],
        date: new Date().getFullYear().toString(),
      }
    );
  }

  /**
   * Updates card data and regenerates textures
   *
   * This method allows for dynamic content updates by:
   * 1. Updating the card data
   * 2. Regenerating all textures
   * 3. Updating existing tile programs with new textures
   *
   * @param newCardData - The new card data to use
   * @returns Promise that resolves when update is complete
   */
  public async updateCardData(newCardData: CardData[]): Promise<void> {
    this.host.cardData = newCardData;
    await this.generateTexturesForCardData();
    this.updateTileTextures();
  }

  /**
   * Updates all tile textures with newly generated textures
   * This is called after card data changes to refresh the display
   */
  private updateTileTextures(): void {
    this.host.tileGroupsData.forEach((_, groupIndex) => {
      for (let row = 0; row < this.host.GRID_ROWS; row++) {
        for (let col = 0; col < this.host.GRID_COLS; col++) {
          const tileIndex = row * this.host.GRID_COLS + col;
          const tileKey = this.getTileKey(groupIndex, tileIndex);

          // Update foreground mesh texture
          const foregroundMesh = this.host.foregroundMeshMap.get(tileKey);
          if (foregroundMesh && foregroundMesh.program) {
            const newForegroundTexture = this.getCardForegroundTexture(groupIndex, tileIndex);
            if (newForegroundTexture) {
              foregroundMesh.program.uniforms.map.value = newForegroundTexture;
            }
          }

          // Update background mesh texture
          const backgroundMesh = this.host.backgroundMeshMap.get(tileKey);
          if (backgroundMesh && backgroundMesh.program) {
            const newBackgroundTexture = this.getCardBackgroundTexture(groupIndex, tileIndex);
            if (newBackgroundTexture) {
              backgroundMesh.program.uniforms.map.value = newBackgroundTexture;
            }
          }
        }
      }
    });
  }

  /**
   * Gets all interactive meshes (foreground meshes that can be clicked)
   * @returns Array of foreground meshes
   */
  public getInteractiveMeshes(): Mesh[] {
    return Array.from(this.host.foregroundMeshMap.values());
  }

  /**
   * Clears all grid data and meshes
   * This is useful for cleanup or reinitialization
   */
  public clear(): void {
    // Clear tile group data
    this.host.tileGroupsData = [];

    // Clear group objects
    this.host.groupObjects.forEach((group) => {
      if (group && group.parent) {
        group.parent.removeChild(group);
      }
    });
    this.host.groupObjects = [];

    // Clear mesh maps
    this.host.foregroundMeshMap.clear();
    this.host.backgroundMeshMap.clear();

    // Clear uniforms and textures
    this.host.staticUniforms.clear();
    this.host.cardTextures = [];

    this.isInitialized = false;
  }

  /**
   * Gets the initialization status
   */
  public get initialized(): boolean {
    return this.isInitialized;
  }

  /**
   * Gets statistics about the current grid
   * @returns Object containing grid statistics
   */
  public getGridStats(): {
    totalGroups: number;
    tilesPerGroup: number;
    totalTiles: number;
    totalTextures: number;
    memoryEstimate: string;
  } {
    const tilesPerGroup = this.host.GRID_COLS * this.host.GRID_ROWS;
    const totalTiles = this.host.tileGroupsData.length * tilesPerGroup;
    const totalTextures = this.host.cardTextures.length * 2; // foreground + background

    // Rough memory estimate (512x512 RGBA textures)
    const bytesPerTexture = 512 * 512 * 4; // RGBA
    const totalMemoryBytes = totalTextures * bytesPerTexture;
    const memoryMB = (totalMemoryBytes / (1024 * 1024)).toFixed(2);

    return {
      totalGroups: this.host.tileGroupsData.length,
      tilesPerGroup,
      totalTiles,
      totalTextures,
      memoryEstimate: `${memoryMB} MB`,
    };
  }
}

/* eslint-disable @typescript-eslint/no-explicit-any */
/**
 * @fileoverview InfiniteGridClass - OGL Infinite Scrolling Grid System
 *
 * A comprehensive OGL-based infinite grid system that creates a seamless,
 * scrollable interface for displaying card-based content. The system uses a
 * 3x3 tile group architecture to create the illusion of infinite content while
 * maintaining optimal performance.
 *
 * Key Features:
 * - Infinite scrolling in all directions
 * - Interactive hover effects with background blur
 * - Click events with custom data dispatching
 * - Post-processing visual effects (distortion, vignette)
 * - GSAP-powered smooth animations and inertia
 * - Responsive viewport calculations
 * - Memory-efficient tile repositioning system
 *
 * Architecture Overview:
 * - Uses 9 tile groups arranged in a 3x3 pattern
 * - Each group contains a configurable grid of individual tiles
 * - Groups are repositioned as the user scrolls to maintain infinite effect
 * - Each tile has foreground (content) and background (blur) layers
 * - Textures are generated dynamically from card data using Canvas 2D API
 */

import { Renderer, Camera, Transform, Mesh, Vec2, RenderTarget, Raycast } from "ogl";
import { gsap } from "gsap";
import { InertiaPlugin } from "gsap/InertiaPlugin";
import { CustomPostProcessShader } from "./PostProcessShader";
import { EventHandler, type EventHandlerHost } from "./EventHandler";
import { DisposalManager, type DisposableHost } from "./DisposalManager";
import { GridManager, type GridManagerHost } from "./GridManager";

import type {
  CardData,
  InfiniteGridOptions,
  Position2D,
  ScrollState,
  TileGroupData,
  CardTexturePair,
  Viewport,
} from "./types.ts";

gsap.registerPlugin(InertiaPlugin);

/**
 * InfiniteGridClass - An OGL-based infinite scrolling grid system
 *
 * This class creates an infinite, scrollable grid of tiles that displays card data.
 * It uses a 3x3 grid system where tiles are repositioned as the user scrolls to
 * create an infinite scrolling effect. Each tile can display custom content
 * with foreground and background textures.
 *
 * Key Features:
 * - Infinite scrolling in all directions using tile repositioning
 * - Interactive hover effects with background blur transitions
 * - Click events with custom event dispatching
 * - Optional post-processing effects (distortion, vignette)
 * - GSAP-powered smooth animations and inertia scrolling
 * - Responsive design with automatic viewport calculations
 *
 * @example
 * ```typescript
 * const grid = new InfiniteGridClass(
 *   containerElement,
 *   cardDataArray,
 *   {
 *     gridCols: 3,
 *     gridRows: 3,
 *     enablePostProcessing: true
 *   }
 * );
 * await grid.init();
 * ```
 */
export class InfiniteGridClass implements EventHandlerHost, DisposableHost, GridManagerHost {
  /**
   * Core Container and Data Properties
   */

  /** The HTML element that contains the OGL canvas */
  public container: HTMLElement;
  /** Array of card data to be displayed in the grid */
  public cardData: CardData[];
  /** Merged configuration options with defaults applied */
  public options: Required<InfiniteGridOptions>;

  /**
   * Grid Layout Properties (calculated once, read-only)
   */

  /** Gap between tiles in OGL world units */
  public readonly GRID_GAP: number;
  /** Size of each individual tile in OGL world units */
  public readonly TILE_SIZE: number;
  /** Total space occupied by one tile including gap */
  public readonly TILE_SPACE: number;
  /** Number of columns in each grid section */
  public readonly GRID_COLS: number;
  /** Number of rows in each grid section */
  public readonly GRID_ROWS: number;
  /** Total width of one grid section */
  public readonly GRID_WIDTH: number;
  /** Total height of one grid section */
  public readonly GRID_HEIGHT: number;
  /** Total width of all 3 grid sections (for infinite wrapping) */
  private readonly TOTAL_GRID_WIDTH: number;
  /** Total height of all 3 grid sections (for infinite wrapping) */
  private readonly TOTAL_GRID_HEIGHT: number;

  /**
   * OGL Core Rendering Objects
   */

  /** Main OGL scene containing all 3D objects */
  public scene: Transform;
  /** Perspective camera for viewing the scene */
  public camera: Camera | null;
  /** WebGL renderer for drawing to the canvas */
  public renderer: Renderer | null;
  /** 2D pointer coordinates for interaction */
  public pointer: Vec2;
  /** Raycast utility for mouse/touch interaction */
  public raycast: Raycast;

  /**
   * Post-Processing Objects
   */

  /** Post-processing shader for visual effects (distortion, vignette) */
  public postProcessShader: CustomPostProcessShader | null;
  /** Render target for capturing the scene before post-processing */
  public sceneRenderTarget: RenderTarget | null;

  /**
   * Scene Objects and Data Structures
   */

  /** Array of OGL transforms, each containing one 3x3 section of tiles */
  public groupObjects: Transform[];
  /** Map of tile keys to foreground mesh objects (clickable content) */
  public foregroundMeshMap: Map<string, Mesh>;
  /** Map of tile keys to background mesh objects (blur effect on hover) */
  public backgroundMeshMap: Map<string, Mesh>;
  /** Generated textures for all cards (foreground + background pairs) */
  public cardTextures: CardTexturePair[];
  /** Static shader uniforms for background materials */
  public staticUniforms: Map<string, any>;

  /**
   * User Interaction State
   */

  /** Key of the currently hovered tile (empty string if none) */
  public currentHoveredTileKey: string;
  /** Whether the user is currently dragging/scrolling */
  public isDown: boolean;
  public isHoveringCanvas: boolean;
  /** Whether the scene has moved significantly during this interaction */
  public hasMovedSignificantly: boolean;
  /** Position where the current drag started */
  public startPosition: Position2D;
  /** Scroll position when the current drag started */
  public scrollPosition: Position2D;
  /** Current scroll state and behavior settings */
  public scroll: ScrollState;
  /** Direction of scroll movement for infinite wrapping logic */
  private direction: Position2D;
  /** GSAP InertiaPlugin tracker for smooth scroll transitions */
  public scrollTracker: any;

  /**
   * Animation Configuration Constants
   */

  /** Duration of hover transition animations in seconds */
  public readonly hoverTransitionDuration: number;
  /** GSAP easing function for hover animations */
  public readonly hoverEase: string;
  /** Initial opacity of background blur effect (0 = transparent) */
  public readonly initialBackgroundOpacity: number;
  /** Target opacity when background is hovered (1 = fully visible) */
  public readonly hoveredBackgroundOpacity: number;
  /** Maximum movement distance in pixels before click is disabled */
  public readonly maxClickMovement: number;

  /**
   * Animation Frame Management
   */

  /** RequestAnimationFrame ID for the main render loop */
  public animationFrameId: number | null;

  /**
   * Tile Group Data Structure
   */

  /** Array containing position data for all 9 tile groups (3x3 infinite grid) */
  public tileGroupsData: TileGroupData[];

  /**
   * Modular System Components
   */

  /** Event handler for managing user interactions */
  public eventHandler?: EventHandler;
  /** Grid manager for handling grid creation and management */
  public gridManager: GridManager;
  /** Disposal manager for resource cleanup */
  private disposalManager: DisposalManager;

  /**
   * Creates a new InfiniteGridClass instance
   *
   * @param containerElement - HTML element that will contain the OGL canvas
   * @param cardData - Array of card data to display in the grid (defaults to empty array)
   * @param options - Configuration options (merged with defaults)
   *
   * @throws {Error} Throws if containerElement is null or undefined
   *
   * @example
   * ```typescript
   * const container = document.getElementById('grid-container');
   * const cards = [
   *   { title: 'Card 1', badge: 'NEW', description: '...', tags: ['tag1'], date: '2024' },
   *   // ... more cards
   * ];
   * const grid = new InfiniteGridClass(container, cards, {
   *   gridCols: 4,
   *   tileSize: 2.5,
   *   enablePostProcessing: true
   * });
   * ```
   */
  constructor(
    containerElement: HTMLElement,
    cardData: CardData[] = [],
    options: Partial<InfiniteGridOptions> = {},
  ) {
    if (!containerElement) {
      console.error("InfiniteGridClass: Container element is required.");
      throw new Error("Container element is required");
    }

    this.container = containerElement;
    this.cardData = cardData;

    // Merge options with defaults to ensure all properties are defined
    this.options = {
      gridCols: 3,
      gridRows: 3,
      gridGap: 0,
      tileSize: 3,
      baseCameraZ: 10,
      enablePostProcessing: true,
      postProcessParams: {
        distortionIntensity: 0.0,
        vignetteOffset: 1.2, // Set outside screen bounds to disable vignette initially
        vignetteDarkness: 1.5,
        ...options.postProcessParams,
      },
      ...options,
    };

    // Initialize grid properties
    this.GRID_GAP = this.options.gridGap;
    this.TILE_SIZE = this.options.tileSize;
    this.TILE_SPACE = this.TILE_SIZE + this.GRID_GAP;
    this.GRID_COLS = this.options.gridCols;
    this.GRID_ROWS = this.options.gridRows;
    this.GRID_WIDTH = this.TILE_SPACE * this.GRID_COLS;
    this.GRID_HEIGHT = this.TILE_SPACE * this.GRID_ROWS;
    this.TOTAL_GRID_WIDTH = this.GRID_WIDTH * 3;
    this.TOTAL_GRID_HEIGHT = this.GRID_HEIGHT * 3;

    // Initialize OGL objects
    this.scene = new Transform();
    this.camera = null;
    this.renderer = null;
    this.pointer = new Vec2();
    this.raycast = new Raycast();

    // Initialize post-processing
    this.postProcessShader = null;
    this.sceneRenderTarget = null;

    // Initialize maps and arrays
    this.groupObjects = [];
    this.foregroundMeshMap = new Map();
    this.backgroundMeshMap = new Map();
    this.cardTextures = [];
    this.staticUniforms = new Map();

    // Initialize interaction state
    this.currentHoveredTileKey = "";
    this.isDown = false;
    this.isHoveringCanvas = false;
    this.hasMovedSignificantly = false;
    this.startPosition = { x: 0, y: 0 };
    this.scrollPosition = { x: 0, y: 0 };
    this.scroll = {
      scale: 0.012,
      current: { x: 0, y: 0 },
      last: { x: 0, y: 0 },
    };
    this.direction = { x: 0, y: 0 };
    this.scrollTracker = InertiaPlugin.track(this.scroll.current, "x,y")[0];

    // Initialize animation properties
    this.hoverTransitionDuration = 0.6;
    this.hoverEase = "power2.out";
    this.initialBackgroundOpacity = 0.0;
    this.hoveredBackgroundOpacity = 1.0;
    this.maxClickMovement = 5; // pixels

    this.animationFrameId = null;
    this.tileGroupsData = [];

    // Initialize modular components
    this.eventHandler = new EventHandler(this);
    this.gridManager = new GridManager(this);
    this.disposalManager = new DisposalManager(this);

    // Bind remaining methods to maintain proper 'this' context
    this.render = this.render.bind(this);
  }

  /**
   * Initializes the infinite grid system asynchronously
   *
   * This method sets up all necessary components in the correct order:
   * 1. WebGL renderer and camera
   * 2. Tile group positioning structure
   * 3. Texture generation for all card data
   * 4. 3D mesh creation and scene setup
   * 5. Event listeners for interaction
   * 6. Animation systems and render loop
   *
   * @returns Promise that resolves when initialization is complete
   *
   * @example
   * ```typescript
   * const grid = new InfiniteGridClass(container, cardData);
   * await grid.init(); // Wait for all textures to load and scene to be ready
   * // Grid is now interactive and rendering
   * ```
   */
  public async init(): Promise<void> {
    this.setupRenderer();
    this.setupCamera();
    this.setupPostProcessing();

    // Initialize grid using the modular system
    await this.gridManager.initialize();

    // Initialize event handling using the modular system
    if (this.eventHandler) {
      this.eventHandler.initialize();
    }

    this.animateInertiaScroll();
    this.animatePostProcessing(
      -0.1, // Target distortionIntensity
      0.3, // Target vignetteOffset (where vignette starts - should be < 1.0)
      1.25, // Target vignetteDarkness (where vignette reaches max - should be > vignetteOffset)
      1.5, // Duration
      1.5, // Delay
      "power3.out", // Ease
    );

    this.updatePositions();
    this.render();
  }

  /**
   * Sets up the WebGL renderer with optimal settings
   *
   * Creates an OGL renderer with antialiasing and transparency,
   * configures it for the container size, and appends the canvas
   * to the DOM.
   */
  private setupRenderer(): void {
    const gl =
      this.container.ownerDocument.createElement("canvas").getContext("webgl2") ||
      this.container.ownerDocument.createElement("canvas").getContext("webgl");
    if (!gl) {
      throw new Error("WebGL not supported");
    }

    this.renderer = new Renderer({
      canvas: gl.canvas as HTMLCanvasElement,
      width: this.container.clientWidth,
      height: this.container.clientHeight,
      dpr: window.devicePixelRatio,
      alpha: true,
      antialias: true,
    });

    // Set canvas styles
    this.renderer.gl.canvas.style.width = "100%";
    this.renderer.gl.canvas.style.height = "100%";

    this.container.appendChild(this.renderer.gl.canvas);
  }

  /**
   * Sets up the perspective camera with proper positioning
   *
   * Creates a perspective camera with a 45-degree field of view,
   * positions it at the configured Z distance.
   */
  private setupCamera(): void {
    const aspectRatio = this.container.clientWidth / this.container.clientHeight;
    this.camera = new Camera(this.renderer!.gl, {
      fov: 45,
      aspect: aspectRatio,
      near: 1,
      far: 1000,
    });
    this.camera.position.set(0, 0, this.options.baseCameraZ);
  }

  /**
   * Sets up post-processing effects if enabled
   *
   * Creates a render target for capturing the scene before post-processing
   * and initializes the post-processing shader with configured parameters.
   */
  private setupPostProcessing(): void {
    if (!this.options.enablePostProcessing || !this.renderer) {
      return;
    }

    // Create render target for capturing the scene
    this.sceneRenderTarget = new RenderTarget(this.renderer.gl, {
      width: this.container.clientWidth,
      height: this.container.clientHeight,
    });

    // Create post-processing shader
    this.postProcessShader = new CustomPostProcessShader(
      this.renderer.gl as any, // Type assertion needed for OGL context
      this.options.postProcessParams,
    );
  }

  /**
   * Calculates the viewport dimensions in world space
   *
   * Uses the camera's field of view and position to determine how much
   * world space is visible. This is essential for infinite scrolling
   * calculations to know when tile groups need to be repositioned.
   *
   * @returns Viewport object with width and height in world units
   */
  private get viewport(): Viewport {
    if (!this.camera) {
      return { width: 0, height: 0 };
    }
    const fov = this.camera.fov * (Math.PI / 180); // Convert FOV to radians
    const viewHeight = 2 * Math.tan(fov / 2) * this.camera.position.z;
    return { width: viewHeight * this.camera.aspect, height: viewHeight };
  }

  public updatePositions(): void {
    const scrollX = this.scroll.current.x;
    const scrollY = this.scroll.current.y;

    // Update direction based on scroll movement
    if (this.scroll.current.y > this.scroll.last.y) {
      this.direction.y = -1;
    } else if (this.scroll.current.y < this.scroll.last.y) {
      this.direction.y = 1;
    } else {
      this.direction.y = 0;
    }

    if (this.scroll.current.x > this.scroll.last.x) {
      this.direction.x = -1;
    } else if (this.scroll.current.x < this.scroll.last.x) {
      this.direction.x = 1;
    } else {
      this.direction.x = 0;
    }

    this.tileGroupsData.forEach((groupData, i) => {
      const groupObject = this.groupObjects[i];

      if (groupObject) {
        const posX = groupData.basePos.x + scrollX + groupData.offset.x;
        const posY = groupData.basePos.y + scrollY + groupData.offset.y;

        const groupOffX = this.GRID_WIDTH / 2;
        const groupOffY = this.GRID_HEIGHT / 2;
        const viewportOff = {
          x: this.viewport.width / 2,
          y: this.viewport.height / 2,
        };

        // Handle infinite scrolling wrapping
        if (this.direction.x < 0 && posX - groupOffX > viewportOff.x) {
          groupData.offset.x -= this.TOTAL_GRID_WIDTH;
        } else if (this.direction.x > 0 && posX + groupOffX < -viewportOff.x) {
          groupData.offset.x += this.TOTAL_GRID_WIDTH;
        }

        if (this.direction.y < 0 && posY - groupOffY > viewportOff.y) {
          groupData.offset.y -= this.TOTAL_GRID_HEIGHT;
        } else if (this.direction.y > 0 && posY + groupOffY < -viewportOff.y) {
          groupData.offset.y += this.TOTAL_GRID_HEIGHT;
        }

        groupObject.position.x = groupData.basePos.x + scrollX + groupData.offset.x;
        groupObject.position.y = groupData.basePos.y + scrollY + groupData.offset.y;
        groupObject.position.z = groupData.basePos.z;
      }
    });
  }

  public animateInertiaScroll(vx: number | string = "auto", vy: number | string = "auto"): void {
    gsap.to(this.scroll.current, {
      inertia: {
        x: vx,
        y: vy,
        min: 60,
        resistance: 100,
      },
      ease: "power2.out",
      onUpdate: () => this.updatePositions(),
      onComplete: () => {
        this.direction.x = 0;
        this.direction.y = 0;
      },
    });
  }

  /**
   * Gets all interactive meshes for raycasting
   * @returns Array of foreground meshes that can be interacted with
   */
  public getInteractiveMeshes(): Mesh[] {
    return this.gridManager.getInteractiveMeshes();
  }

  /**
   * Updates mouse/touch coordinates for raycasting
   * @param clientX - X coordinate in client space
   * @param clientY - Y coordinate in client space
   */
  public updatePointerCoordinates(clientX: number, clientY: number): void {
    if (!this.renderer) return;

    // Convert to normalized device coordinates (-1 to 1)
    const rect = this.renderer.gl.canvas.getBoundingClientRect();
    const x = ((clientX - rect.left) / rect.width) * 2 - 1;
    const y = -(((clientY - rect.top) / rect.height) * 2 - 1); // Flip Y coordinate

    this.pointer.set(x, y);
  }

  /**
   * Performs raycasting and returns hit results
   * @returns Array of hit meshes ordered by distance
   */
  public performRaycast(): Mesh[] {
    if (!this.camera || !this.renderer) return [];

    // Update raycast with current camera and mouse position
    this.raycast.castMouse(this.camera, this.pointer);

    // Get all interactive meshes
    const meshes = this.getInteractiveMeshes();

    // Perform intersection test
    const hits = this.raycast.intersectBounds(meshes);

    return hits;
  }

  /**
   * Extracts tile key from a mesh using its userData
   * @param mesh - The mesh to get the tile key from
   * @returns The tile key or empty string if not found
   */
  public getTileKeyFromMesh(mesh: Mesh): string {
    return this.gridManager.getTileKeyFromMesh(mesh);
  }

  public fadeInBackground(mesh: Mesh): void {
    if (mesh.program && mesh.program.uniforms.uOpacity) {
      gsap.to(mesh.program.uniforms.uOpacity, {
        value: this.hoveredBackgroundOpacity,
        duration: this.hoverTransitionDuration,
        ease: this.hoverEase,
        overwrite: true,
      });
    }
  }

  public fadeOutBackground(mesh: Mesh): void {
    if (mesh.program && mesh.program.uniforms.uOpacity) {
      gsap.to(mesh.program.uniforms.uOpacity, {
        value: this.initialBackgroundOpacity,
        duration: this.hoverTransitionDuration,
        ease: this.hoverEase,
        overwrite: true,
      });
    }
  }

  /**
   * Gets the card data for a specific tile
   * @param groupIndex - The group index of the tile
   * @param tileIndex - The tile index within the group
   * @returns The card data for the tile
   */
  public getCardDataForTile(groupIndex: number, tileIndex: number): CardData {
    return this.gridManager.getCardDataForTile(groupIndex, tileIndex);
  }

  private render(): void {
    this.scroll.last.x = this.scroll.current.x;
    this.scroll.last.y = this.scroll.current.y;
    this.updatePositions();

    if (this.renderer && this.camera) {
      if (this.options.enablePostProcessing && this.postProcessShader && this.sceneRenderTarget) {
        // Render scene to render target first
        this.renderer.render({
          scene: this.scene,
          camera: this.camera,
          target: this.sceneRenderTarget,
        });

        // Debug: Check if render target has a texture
        if (!this.sceneRenderTarget.texture) {
          console.error("PostProcessing: Render target has no texture");
        }

        // Set the rendered scene as input to post-processing shader
        this.postProcessShader.setInputTexture(this.sceneRenderTarget.texture);

        // Render post-processing effect to screen
        this.postProcessShader.render(null); // null = render to screen
      } else {
        // Direct render without post-processing
        this.renderer.render({ scene: this.scene, camera: this.camera });
      }
    }

    this.animationFrameId = requestAnimationFrame(this.render);
  }

  /**
   * Animates the post-processing effects
   *
   * @param targetDistortion - Target distortion intensity (0 = no distortion)
   * @param targetVignetteOffset - Target vignette offset (0.0-1.0)
   * @param targetVignetteDarkness - Target vignette darkness (should be > offset)
   * @param duration - Animation duration in seconds
   * @param delay - Animation delay in seconds
   * @param ease - GSAP ease function
   *
   * @example
   * ```typescript
   * // Animate to strong distortion and vignette
   * grid.animatePostProcessing(0.5, 0.6, 0.9, 2.0);
   *
   * // Reset to no effects
   * grid.animatePostProcessing(0, 0.8, 1.0, 1.0);
   * ```
   */
  public animatePostProcessing(
    targetDistortion: number,
    targetVignetteOffset: number,
    targetVignetteDarkness: number,
    duration: number = 1,
    delay: number = 0,
    ease: string = "power2.out",
  ): void {
    if (this.postProcessShader) {
      this.postProcessShader.animate(
        targetDistortion,
        targetVignetteOffset,
        targetVignetteDarkness,
        duration,
        delay,
        ease,
      );
    }
  }

  /**
   * Toggles post-processing on/off for debugging
   * @param enabled - Whether to enable post-processing
   */
  public setPostProcessingEnabled(enabled: boolean): void {
    this.options.enablePostProcessing = enabled;
  }

  /**
   * Gets the current distortion intensity
   */
  public get distortionIntensity(): number {
    return this.postProcessShader?.distortionIntensity ?? 0;
  }

  /**
   * Sets the distortion intensity (0 = no distortion)
   */
  public set distortionIntensity(value: number) {
    if (this.postProcessShader) {
      this.postProcessShader.distortionIntensity = value;
    }
  }

  /**
   * Gets the current vignette offset
   */
  public get vignetteOffset(): number {
    return this.postProcessShader?.vignetteOffset ?? 0.8;
  }

  /**
   * Sets the vignette offset (0.0 = center, 1.0 = edges)
   */
  public set vignetteOffset(value: number) {
    if (this.postProcessShader) {
      this.postProcessShader.vignetteOffset = value;
    }
  }

  /**
   * Gets the current vignette darkness
   */
  public get vignetteDarkness(): number {
    return this.postProcessShader?.vignetteDarkness ?? 1.0;
  }

  /**
   * Sets the vignette darkness (should be > vignetteOffset)
   */
  public set vignetteDarkness(value: number) {
    if (this.postProcessShader) {
      this.postProcessShader.vignetteDarkness = value;
    }
  }

  /**
   * Completely disposes of the infinite grid and cleans up all resources
   *
   * This method now uses the DisposalManager for systematic cleanup to prevent memory leaks.
   * The disposal manager handles:
   * - Cancels the animation frame loop
   * - Removes all event listeners
   * - Disposes all OGL geometries, materials, and textures
   * - Clears all data structures and maps
   * - Removes the canvas from the DOM
   *
   * Call this method when the grid is no longer needed, such as when
   * navigating away from the page or unmounting the component.
   *
   * @example
   * ```typescript
   * // In a Vue component's onBeforeUnmount or React's useEffect cleanup
   * onBeforeUnmount(() => {
   *   if (gridInstance) {
   *     gridInstance.dispose();
   *     gridInstance = null;
   *   }
   * });
   * ```
   */
  public dispose(): void {
    this.disposalManager.dispose();
  }

  /**
   * Gets the event handler instance for advanced event management
   * @returns The EventHandler instance or undefined if not initialized
   */
  public getEventHandler(): EventHandler | undefined {
    return this.eventHandler;
  }

  /**
   * Gets the grid manager instance for advanced grid management
   * @returns The GridManager instance
   */
  public getGridManager(): GridManager {
    return this.gridManager;
  }

  /**
   * Gets the disposal manager instance for advanced cleanup control
   * @returns The DisposalManager instance
   */
  public getDisposalManager(): DisposalManager {
    return this.disposalManager;
  }

  /**
   * Updates card data and regenerates the grid
   * @param newCardData - The new card data to display
   * @returns Promise that resolves when update is complete
   */
  public async updateCardData(newCardData: CardData[]): Promise<void> {
    this.cardData = newCardData;
    await this.gridManager.updateCardData(newCardData);
  }

  /**
   * Gets statistics about the current grid
   * @returns Object containing grid statistics
   */
  public getGridStats() {
    return this.gridManager.getGridStats();
  }

  /**
   * Validates that all resources have been properly disposed
   * Useful for debugging memory leaks
   * @returns True if disposal was successful, false if issues were found
   */
  public validateDisposal(): boolean {
    return this.disposalManager.validateDisposal();
  }

  /**
   * Performs a partial cleanup that preserves the core structure
   * but clears dynamic content. Useful for reinitialization scenarios.
   */
  public partialCleanup(): void {
    this.disposalManager.partialCleanup();
  }
}

import {
  Program,
  RenderTarget,
  Mesh,
  Plane,
  Vec2,
  Renderer,
  Transform,
  Camera,
  Texture,
} from "ogl";
import { gsap } from "gsap";

// Import shaders as raw strings
import { postProcessVertexShader, postProcessFragmentShader } from "./shaders";

// OGL context type
type OGLContext = WebGL2RenderingContext & { renderer: Renderer; canvas: HTMLCanvasElement };

/**
 * @interface CustomPostProcessShaderParameters
 * @description Defines the initial parameters for the CustomPostProcessShader.
 * @property {number} [distortionIntensity] - Initial intensity of the distortion effect. Defaults to 0.
 * @property {number} [vignetteOffset] - Initial offset for the vignette effect (0.0 = center, 1.0 = edges). Defaults to 0.8.
 * @property {number} [vignetteDarkness] - Controls vignette transition smoothness (should be > vignetteOffset). Defaults to 1.0.
 */
interface CustomPostProcessShaderParameters {
  distortionIntensity?: number;
  vignetteOffset?: number;
  vignetteDarkness?: number;
}

/**
 * @class CustomPostProcessShader
 * @description A custom OGL-based post-processing shader for distortion and vignette effects.
 * It provides animatable properties for these effects via GSAP.
 */
export class CustomPostProcessShader {
  private gl: OGLContext;
  private program: Program | null;
  private renderTarget: RenderTarget | null;
  private mesh: Mesh | null;
  private geometry: Plane | null;
  private scene: Transform | null;
  private camera: Camera | null;

  /**
   * @private
   * @property {number} _distortionIntensity - Internal property for distortion intensity, animated by GSAP.
   */
  private _distortionIntensity: number = 0;

  /**
   * @private
   * @property {number} _vignetteOffset - Internal property for vignette offset (0.0-1.0 range), animated by GSAP.
   */
  private _vignetteOffset: number = 0.8;

  /**
   * @private
   * @property {number} _vignetteDarkness - Internal property for vignette transition end (should be > _vignetteOffset), animated by GSAP.
   */
  private _vignetteDarkness: number = 1.0;

  /**
   * Creates an instance of CustomPostProcessShader.
   * @param {OGLContext} gl - The OGL WebGL context
   * @param {CustomPostProcessShaderParameters} [initialParams={}] - Optional initial parameters for the shader effects.
   */
  constructor(gl: OGLContext, initialParams: CustomPostProcessShaderParameters = {}) {
    this.gl = gl;

    // Initialize internal properties from constructor parameters
    this._distortionIntensity = initialParams.distortionIntensity ?? 0;
    this._vignetteOffset = initialParams.vignetteOffset ?? 1.2;
    this._vignetteDarkness = initialParams.vignetteDarkness ?? 1.5;

    // Create render target
    this.renderTarget = new RenderTarget(gl, {
      width: gl.canvas.width,
      height: gl.canvas.height,
    });

    // Create geometry for full-screen quad
    this.geometry = new Plane(gl, {
      width: 2,
      height: 2,
    });

    // Create shader program
    this.program = new Program(gl, {
      vertex: postProcessVertexShader,
      fragment: postProcessFragmentShader,
      uniforms: {
        tDiffuse: { value: null }, // The input texture from the previous pass
        distortion: { value: new Vec2(0, 0) }, // This will be calculated from _distortionIntensity
        vignetteOffset: { value: this._vignetteOffset },
        vignetteDarkness: { value: this._vignetteDarkness },
      },
      transparent: false,
      cullFace: false,
    });

    // Create mesh
    this.mesh = new Mesh(gl, {
      geometry: this.geometry,
      program: this.program,
    });

    // Create a scene for the post-processing mesh
    this.scene = new Transform();
    this.mesh.setParent(this.scene);

    // Create an orthographic camera for post-processing
    this.camera = new Camera(gl, {
      left: -1,
      right: 1,
      bottom: -1,
      top: 1,
      near: 0,
      far: 2,
    });
    this.camera.position.set(0, 0, 1);

    // Immediately update uniforms based on initial values
    this.updateUniforms();
  }

  /**
   * @property {number} distortionIntensity - Getter/Setter for the distortion intensity.
   * When set, it updates the shader uniforms.
   */
  get distortionIntensity(): number {
    return this._distortionIntensity;
  }

  set distortionIntensity(value: number) {
    this._distortionIntensity = value;
    this.updateUniforms();
  }

  /**
   * @property {number} vignetteOffset - Getter/Setter for the vignette offset.
   * Controls where the vignette effect starts (0.0 = center, 1.0 = screen edges).
   * When set, it updates the shader uniforms.
   */
  get vignetteOffset(): number {
    return this._vignetteOffset;
  }

  set vignetteOffset(value: number) {
    this._vignetteOffset = value;
    this.updateUniforms();
  }

  /**
   * @property {number} vignetteDarkness - Getter/Setter for the vignette transition end.
   * Controls where the vignette reaches maximum darkness (should be > vignetteOffset for smooth transition).
   * When set, it updates the shader uniforms.
   */
  get vignetteDarkness(): number {
    return this._vignetteDarkness;
  }

  set vignetteDarkness(value: number) {
    this._vignetteDarkness = value;
    this.updateUniforms();
  }

  /**
   * @method updateUniforms
   * @description Updates all relevant uniforms of the shader material based on the current
   * internal `_distortionIntensity`, `_vignetteOffset`, and `_vignetteDarkness` properties.
   * This method should be called whenever the internal properties change to reflect
   * the changes in the shader.
   */
  updateUniforms(): void {
    if (!this.program) return;

    // Calculate distortion uniform based on current distortionIntensity and aspect ratio.
    // We'll use the current window aspect ratio, which is common for full-screen effects.
    const aspectRatio = window.innerWidth / window.innerHeight;
    // The distortion uniform is a vec2. We'll apply the intensity scaled by aspect ratio
    // to the x component and directly to the y component, allowing for non-uniform scaling
    // if the shader utilizes both.
    // Assuming the shader's `distortion.x` is the primary scalar for the effect,
    // and `distortion.y` can be used for secondary axis scaling or ignored.
    this.program.uniforms.distortion.value.set(
      this._distortionIntensity * aspectRatio,
      this._distortionIntensity,
    );

    this.program.uniforms.vignetteOffset.value = this._vignetteOffset;
    this.program.uniforms.vignetteDarkness.value = this._vignetteDarkness;
  }

  /**
   * @method animate
   * @description Animates the distortion and vignette parameters using GSAP.
   * @param {number} targetDistortion - The target distortion intensity value.
   * @param {number} targetVignetteOffset - The target vignette offset value.
   * @param {number} targetVignetteDarkness - The target vignette darkness value.
   * @param {number} [duration=1] - The duration of the animation in seconds.
   * @param {number} [delay=0] - The delay before the animation starts in seconds.
   * @param {string} [ease='power2.out'] - The GSAP ease function to use for the animation.
   */
  animate(
    targetDistortion: number,
    targetVignetteOffset: number,
    targetVignetteDarkness: number,
    duration: number = 1,
    delay: number = 0,
    ease: string = "power2.out",
  ): void {
    gsap.to(this, {
      distortionIntensity: targetDistortion,
      vignetteOffset: targetVignetteOffset,
      vignetteDarkness: targetVignetteDarkness,
      duration: duration,
      delay: delay,
      ease: ease,
      onUpdate: () => this.updateUniforms(), // Ensure uniforms are updated during animation
    });
  }

  /**
   * @method setInputTexture
   * @description Sets the input texture for post-processing
   * @param {any} texture - The input texture (usually from a RenderTarget)
   */
  setInputTexture(texture: Texture): void {
    if (!this.program) return;
    this.program.uniforms.tDiffuse.value = texture;
  }

  /**
   * @method render
   * @description Renders the post-processing effect to the specified target or screen
   * @param {RenderTarget | null} target - The render target (null for screen)
   */
  render(target: RenderTarget | null = null): void {
    if (!this.scene || !this.camera) return;

    // Use OGL's renderer to render the post-processing scene
    const renderer = this.gl.renderer;
    if (target) {
      renderer.render({ scene: this.scene, camera: this.camera, target });
    } else {
      renderer.render({ scene: this.scene, camera: this.camera });
    }
  }

  /**
   * @method resize
   * @description Resizes the render target when the canvas size changes
   * @param {number} width - New width
   * @param {number} height - New height
   */
  resize(width: number, height: number): void {
    if (!this.renderTarget) return;
    this.renderTarget.setSize(width, height);
    this.updateUniforms();
  }

  /**
   * @method dispose
   * @description Cleans up WebGL resources
   */
  dispose(): void {
    // OGL resources are automatically cleaned up by the WebGL context
    // We just need to release references
    this.renderTarget = null;
    this.geometry = null;
    this.program = null;
    this.mesh = null;
    this.scene = null;
    this.camera = null;
  }
}

export const gaussianBlurFragmentShader = /* glsl */ `precision highp float;

uniform sampler2D map;
// uniform float blurAmount; // Removed: Blur amount is now fixed
uniform vec2 resolution;
uniform float uOpacity; // New uniform for controlling opacity from JS

varying vec2 vUv;

void main() {
    vec2 onePixel = vec2(1.0, 1.0) / resolution;

    // Fixed blur amount directly in the shader
    float fixedBlurAmount = 10.0; // Changed to a fixed value as requested

    vec4 sum = vec4(0.0);

    sum += texture2D(map, vUv + onePixel * vec2(-1.0, -1.0) * fixedBlurAmount) * 0.0625;
    sum += texture2D(map, vUv + onePixel * vec2( 0.0, -1.0) * fixedBlurAmount) * 0.125;
    sum += texture2D(map, vUv + onePixel * vec2( 1.0, -1.0) * fixedBlurAmount) * 0.0625;
    sum += texture2D(map, vUv + onePixel * vec2(-1.0,  0.0) * fixedBlurAmount) * 0.125;
    sum += texture2D(map, vUv + onePixel * vec2( 0.0,  0.0) * fixedBlurAmount) * 0.25;
    sum += texture2D(map, vUv + onePixel * vec2( 1.0,  0.0) * fixedBlurAmount) * 0.125;
    sum += texture2D(map, vUv + onePixel * vec2(-1.0,  1.0) * fixedBlurAmount) * 0.0625;
    sum += texture2D(map, vUv + onePixel * vec2( 0.0,  1.0) * fixedBlurAmount) * 0.125;
    sum += texture2D(map, vUv + onePixel * vec2( 1.0,  1.0) * fixedBlurAmount) * 0.0625;

    // Apply the opacity uniform to the alpha channel of the final color
    gl_FragColor = vec4(sum.rgb, sum.a * uOpacity); // sum.a is usually 1.0 from texture, so multiply by uOpacity
}`;

export const gaussianBlurVertexShader = `
  attribute vec2 uv;
  attribute vec3 position;
  
  uniform mat4 modelViewMatrix;
  uniform mat4 projectionMatrix;
  
  varying vec2 vUv;
  
  void main() {
    // Flip UV coordinates 180 degrees (both X and Y)
    vUv = vec2(uv.x, 1.0 - uv.y);
    gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
  }
`;

export const postProcessFragmentShader = /* glsl */ `precision highp float;

uniform sampler2D tDiffuse;
uniform vec2 distortion;
uniform float vignetteOffset;
uniform float vignetteDarkness;

varying vec2 vUv;

void main() {
    vec2 shiftedUv = 2.0 * (vUv - 0.5);
    float distanceToCenter = length(shiftedUv);

    // Lens distortion effect
    // NOTE: The original shader had 'distortion * dot(shiftedUv)'.
    // If distortion is a vec2, dot product will result in a scalar.
    // If you intend distortion to scale both X and Y independently based on distance,
    // you might need something like: shiftedUv *= (0.88 + distortion.x * abs(shiftedUv.x) + distortion.y * abs(shiftedUv.y));
    // For now, I'll keep the dot product as it was in your provided shader,
    // which applies uniform distortion based on radial distance.
    shiftedUv *= (0.88 + distortion.x * dot(shiftedUv, shiftedUv)); // Assuming distortion.x controls the scalar distortion factor
    vec2 transformedUv = shiftedUv * 0.5 + 0.5;

    // Vignette effect
    // Corrected 'vignetteOffset * 0.799' and '(vignetteDarkness + vignetteOffset)' if that was the intent.
    // The second parameter to smoothstep is the "edge" where it starts.
    // I'll interpret your intent as scaling the effect based on a combined factor.
    float vignetteIntensity = smoothstep(vignetteOffset, vignetteDarkness, distanceToCenter); // Simplified as common vignette
    // If your original intention for vignette was 'smoothstep(0.8, vignetteOffset * 0.799, (vignetteDarkness + vignetteOffset) * distanceToCenter);'
    // this would be a more complex interaction. Let's start with a standard vignette.

    // Sample render texture and output fragment
    vec3 color = texture2D(tDiffuse, transformedUv).rgb * (1.0 - vignetteIntensity); // Apply darkening based on intensity
    // The original '* vignetteIntensity' would brighten. Vignettes usually darken.
    // If you want a more subtle darkening, adjust the '(1.0 - vignetteIntensity)''.
    gl_FragColor = vec4(color, 1.);
}`;

export const postProcessVertexShader = /* glsl */ `
attribute vec2 uv;
attribute vec3 position;

uniform mat4 modelViewMatrix;
uniform mat4 projectionMatrix;

varying vec2 vUv;

void main() {
    vUv = uv;
    gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
}`;

/**
 * Type definitions for the Infinite Grid system
 */

import { Texture, Vec3 } from "ogl";

/**
 * Represents the data structure for a single card/tile in the grid
 */
export interface CardData {
  /** The main title text displayed on the card */
  title: string;
  /** Badge text (currently not implemented in rendering) */
  badge: string;
  /** Detailed description text (optional) */
  description?: string;
  /** Array of tag strings displayed as pills at the bottom */
  tags: string[];
  /** Date string displayed in the bottom-right corner */
  date: string;
  /** Optional image URL for the card's main image */
  image?: string;
}

/**
 * Configuration parameters for post-processing visual effects
 */
export interface PostProcessParams {
  /** Intensity of the barrel/pincushion distortion effect (0 = no distortion) */
  distortionIntensity?: number;
  /** Offset value for the vignette effect (higher = smaller dark area) */
  vignetteOffset?: number;
  /** Darkness intensity of the vignette effect (higher = darker edges) */
  vignetteDarkness?: number;
}

/**
 * Configuration options for initializing the infinite grid
 */
export interface InfiniteGridOptions {
  /** Number of columns in each grid section (default: 3) */
  gridCols?: number;
  /** Number of rows in each grid section (default: 3) */
  gridRows?: number;
  /** Gap between tiles in Three.js units (default: 0) */
  gridGap?: number;
  /** Size of each tile in Three.js units (default: 3) */
  tileSize?: number;
  /** Base Z position of the camera (default: 10) */
  baseCameraZ?: number;
  /** Whether to enable post-processing effects (default: true) */
  enablePostProcessing: boolean;
  /** Parameters for post-processing effects */
  postProcessParams: PostProcessParams;
}

/**
 * Simple 2D coordinate structure
 */
export interface Position2D {
  /** X coordinate */
  x: number;
  /** Y coordinate */
  y: number;
}

/**
 * Tracks the current scroll state and behavior
 */
export interface ScrollState {
  /** Scaling factor for scroll sensitivity */
  scale: number;
  /** Current scroll position */
  current: Position2D;
  /** Previous scroll position for delta calculations */
  last: Position2D;
}

/**
 * Data structure for each group of tiles (3x3 groups create infinite effect)
 */
export interface TileGroupData {
  /** Base 3D position of the group in world space */
  basePos: Vec3;
  /** Additional offset for infinite scrolling wrapping */
  offset: Position2D;
}

/**
 * User data attached to each tile mesh for identification
 */
export interface TileUserData {
  /** Index of the tile group this tile belongs to */
  groupIndex: number;
  /** Index of the tile within its group */
  tileIndex: number;
  /** Unique string identifier for the tile */
  tileKey: string;
}

/**
 * Event detail passed when a tile is clicked
 */
export interface TileClickEventDetail {
  /** Index of the clicked tile's group */
  groupIndex: number;
  /** Index of the clicked tile within its group */
  tileIndex: number;
  /** The card data associated with the clicked tile */
  cardData: CardData;
}

/**
 * Pair of textures for each card (foreground content + background blur)
 */
export interface CardTexturePair {
  /** Canvas texture containing the card's main content */
  foreground: Texture | null;
  /** Canvas texture containing the blurred background image */
  background: Texture | null;
}

/**
 * Camera viewport dimensions in world space
 */
export interface Viewport {
  /** Width of the viewport in world units */
  width: number;
  /** Height of the viewport in world units */
  height: number;
}

// Custom event types
declare global {
  interface HTMLElementEventMap {
    tileClicked: CustomEvent<TileClickEventDetail>;
  }
}

export { default as LightSpeed } from "./LightSpeed.vue";
